mapper.select:
select REPAIR_ID , user.MEMBER_NAME AS  CommitMemberName, REPAIR_COMMIT_REASON, DEVICE_NAME, worker.MEMBER_NAME AS RepairMemberName,ADDRESS_BUILDING,ADDRESS_FLOOR,ADDRESS_ROOM,REPAIR_COMMIT_TIME,REPAIR_TIME,REPAIR_STATUS
from sys_repair left join sys_member user on REPAIR_COMMIT_ID = user.MEMBER_ID
                left join sys_member worker on REPAIR_MEMBER_ID = worker.MEMBER_ID
                left join sys_address address on REPAIR_ADDRESS_ID = address.ADDRESS_ID
                left join sys_device device on REPAIR_DEVICE_ID = device.DEVICE_ID
where 1=1
and REPAIR_ID =  #{Repair.repairId}
and user.MEMBER_NAME like CONCAT('%',#{Repair.commitMemberName} ,'%')
and worker.MEMBER_NAME like CONCAT('%',#{Repair.repairMemberName} ,'%')
and ADDRESS_BUILDING = #{Repair.addressBuilding}
and ADDRESS_FLOOR = #{Repair.addressFloor}
and ADDRESS_ROOM = #{Repair.addressRoom}
and DEVICE_NAME = #{Repair.deviceName}
and REPAIR_STATUS = #{Repair.repairStatus}



            and date_format(REPAIR_COMMIT_TIME,'%Y-%m-%d %H:%i:%s') <= str_to_date(#{endTime},'%Y-%m-%d %H:%i:%s')
            and date_format(REPAIR_COMMIT_TIME,'%Y-%m-%d %H:%i:%s') >= str_to_date(#{startTime},'%Y-%m-%d %H:%i:%s')


            and date_format(REPAIR_TIME,'%Y-%m-%d %H:%i:%s') <= str_to_date(#{endTime},'%Y-%m-%d %H:%i:%s')
            and date_format(REPAIR_TIME,'%Y-%m-%d %H:%i:%s') >= str_to_date(#{startTime},'%Y-%m-%d %H:%i:%s')
